          {items.left.map((item) => (
            <GridItem key={item.position}>
              <div className="grid-item">
                <div className="grid-item-content">
                  {item.name.toUpperCase()}
                </div>
              </div>
            </GridItem>
          ))}
        </GridDropZone>
      </div>
    </GridContextProvider> */}
