import React, { useState } from 'react'
import Navbar from './Navbar'
import "./Puzzle.css"
import {
  GridContextProvider,
  GridDropZone,
  GridItem,
  swap,
  move
} from "react-grid-dnd";
import { Draggable } from 'drag-react';
const Puzzle = () => {
  // get input value
  const [number, setNumber] = useState("");
  const [items, setItems] = React.useState({
    left: [
      { position: 0, name: "ben" },
      { position: 1, name: "joe" },
      { position: 2, name: "jason" },
      { position: 3, name: "chris" },
      { position: 4, name: "heather" }
    ],
    right: [
      { position: 5, name: "george" },
      { position: 6, name: "rupert" },
      { position: 7, name: "alice" },
      { position: 8, name: "katherine" },
      { position: 8, name: "pam" },
      { position: 10, name: "katie" }
    ]
  });

  function onChange(sourceId, sourceIndex, targetIndex, targetId) {
    console.log(targetId, sourceId);
    if (targetId) {
      const result = move(
        items[sourceId],
        items[targetId],
        sourceIndex,
        targetIndex
      );
      return setItems({
        ...items,
        [sourceId]: result[0],
        [targetId]: result[1]
      });
    }
    const result = swap(items[sourceId], sourceIndex, targetIndex);
    return setItems({
      ...items,
      [sourceId]: result
    });
  }

  const generateGrid = () => {
    // get input value
    var number = document.getElementById("inputNumber").value;

    // create array of numbers to populate grid
    var numbers = [];
    for (var i = 1; i <= number * number; i++) {
      numbers.push(i);
    }
    // shuffle numbers array to randomize order
    for (var i = numbers.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      var temp = numbers[i];
      numbers[i] = numbers[j];
      numbers[j] = temp;
    }
    {/* <div class =grid-container>  */ }
    // create grid HTML
    var gridHTML = " <table class='table grid-item t '  > ";
    for (var i = 0; i < number; i++) {
      gridHTML += "<tr  class=' grid-item '>";
      for (var j = 0; j < number; j++) {
        gridHTML += "<td class=' grid-item  '>" + numbers[i * number + j] + "</td>";
      }
      gridHTML += "</tr>";
    }
    gridHTML += "</table>";

    // add grid HTML to container
    document.getElementById("gridContainer").innerHTML = gridHTML;
  }
  return (
    <>
      <Navbar />
      <div className="Main-container-puzzle">
        <form className="Main-Box-puzzle">
          <div className="Main-form-content">
            <h3 className="Main-form-title">Random Grid Generator</h3>

            <div class="container">

              <form>
                <div class="form-group">
                  <label for="inputNumber">Enter a number:</label>
                  <input type="number" class="form-control" id="inputNumber" min="1" max="10" required />
                </div>
                <button type="button" class="btn btn-primary" onClick={generateGrid}>Generate Grid</button>

                
             
              </form>
              <div id="gridContainer">

              </div>


            </div>

          </div>
        </form>
{/* <GridContextProvider onChange={onChange}>
      <div className="container">
        <GridDropZone
          className="dropzone left"
          id="left"
          boxesPerRow={3}
          rowHeight={150}
        >
          {items.left.map((item) => (
            <GridItem key={item.position}>
              <div className="grid-item">
                <div className="grid-item-content">
                  {item.name.toUpperCase()}
                </div>
              </div>
            </GridItem>
          ))}
        </GridDropZone>
      </div>
    </GridContextProvider> */}
    <Draggable>
  <div>My element is draggable now</div>
  <div>My element is draggable now</div>
  <div>My element is draggable now</div>
</Draggable>

      </div>
    </>
  )
}

export default Puzzle
