import React, { useState } from 'react'
import Navbar from './Navbar'

const Puzzle = () => {
     // get input value
    const [number, setNumber]= useState("");

    const generateGrid=()=>{
          // create array of setNumber to populate grid
          var setNumber = [];
          for (var i = 1; i <= number * number; i++) {
              setNumber.push(i);
          }
          // shuffle setNumber array to randomize order
          for (var i = setNumber.length - 1; i > 0; i--) {
              var j = Math.floor(Math.random() * (i + 1));
              var temp = setNumber[i];
              setNumber[i] = setNumber[j];
              setNumber[j] = temp;
          }

          // create grid HTML
          var gridHTML = "<table class='table'>";
          for (var i = 0; i < number; i++) {
              gridHTML += "<tr>";
              for (var j = 0; j < number; j++) {
                  gridHTML += "<td>" + setNumber[i * number + j] + "</td>";
              }
              gridHTML += "</tr>";
          }
          gridHTML += "</table>";

          // add grid HTML to container
          document.getElementById("gridContainer").innerHTML = gridHTML;

    }
  return (
   <>
 <Navbar/>
 <div className="Main-container-puzzle">
