            {/* <img className='Main-logo'src={logo} alt =""/> */}
        
        
          {/* <div className="d-grid gap-2 mt-3">
            <Link to="/puzzle" type="submit" className="btn btn-success">
              Start
            </Link>
          </div> */}
          <div class="container">
    
        <form>
            <div class="form-group">
                <label for="inputNumber">Enter a number:</label>
                <input type="number" onChange={()=>setNumber.target.va} class="form-control" id="inputNumber" min="1" max="10" required/>
            </div>
            <button type="button" class="btn btn-primary" onClick={generateGrid}>Generate Grid</button>
        </form>
        <div id="gridContainer"></div>
    </div>
        
        </div>
      </form>
    </div>
   </>
  )
}

export default Puzzle
