import React, { useState } from 'react'
import Navbar from './Navbar'
import "./Puzzle.css"
const Puzzle = () => {
     // get input value
    const [number, setNumber]= useState("");
    

    const generateGrid=()=>{
         // get input value
         var number = document.getElementById("inputNumber").value;

         // create array of numbers to populate grid
         var numbers = [];
         for (var i = 1; i <= number * number; i++) {
             numbers.push(i);
         }
         // shuffle numbers array to randomize order
         for (var i = numbers.length - 1; i > 0; i--) {
             var j = Math.floor(Math.random() * (i + 1));
             var temp = numbers[i];
             numbers[i] = numbers[j];
             numbers[j] = temp;
         }

         // create grid HTML
         var gridHTML = "<table class='table'  >";
         for (var i = 0; i < number; i++) {
             gridHTML += "<tr>";
             for (var j = 0; j < number; j++) {
                 gridHTML += "<td>" + numbers[i * number + j] + "</td>";
             }
             gridHTML += "</tr>";
         }
         gridHTML += "</table>";

         // add grid HTML to container
         document.getElementById("gridContainer").innerHTML = gridHTML;
     }
  return (
